function init(){gsap.from(".user",{y:-50,duration:.3,stagger:.09,opacity:0,ease:"Power2.Out"})}let container=$(".main");function openChat(){let t,e=$(event.target).attr("userid");$.ajax({type:"get",url:"/profile/getforchat?id="+e,success:function(e){t=e.user},error:function(t){console.log(t.responseText)}}).then(()=>{$(".chatbox").css("transform","translateY(-100%)"),$(".list").css("opacity","0"),$(".chatbox").css("opacity","1"),$(".forWhom")[0].innerText="Message for "+t.name})}function back(){$(".chatbox").css("transform","translateY(0)"),$(".list").css("opacity","1"),$(".chatbox").css("opacity","0")}container.click(()=>{$(event.target)[0]===$(".cancelMessage")[0]&&$(document).trigger("click"),event.stopPropagation()});